INSERT INTO `entity__services` (`id`, `pid`, `idx`, `active`, `untouchable`, `name`, `anons`, `pic_mini`, `main_pic`, `descr`, `descr2`, `pics`, `to_main`) VALUES
(1, 0, 10, 1, 0, 'ДИЗАЙН-ПРОЕКТ «СТИЛЬ+ПЛАН»', 'Разработка планировочных решений,  концептуальное предложение по стилю и отделочным материалам', 'services/854_5613bfc6cb082.jpg', 'services/187_5613c11306e7f.jpg', '<ul>\r\n	<li>Индивидуальная авторская концепция дизайна помещения.</li>\r\n	<li>Стилевое решение интерьера.</li>\r\n	<li>План обмера помещений.</li>\r\n	<li>План сносимых стен &ndash; несколько вариантов.</li>\r\n	<li>План возводимых стен &ndash; несколько вариантов.</li>\r\n	<li>План расстановки мебели &ndash; несколько вариантов.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>', '<h2>ЧТО&nbsp;МЫ&nbsp;ПРЕДЛАГАЕМ:</h2>\r\n\r\n<p>Наша студия всегда открыта для необычных идей и креативных проектов. Мы можем предложить для Вас концепцию декорирования интерьеров для проведения торжественных мероприятий.</p>\r\n\r\n<p>Мы будем рады встрече с Вами у нас в офисе!</p>', '', 1),
(2, 0, 20, 1, 0, 'ДИЗАЙН-ПРОЕКТ «БАЗОВЫЙ»', 'Разработка планировочных решений, предложение по стилю и отделочным материалам, комплект рабочих чертежей', 'services/120_5613c0d092707.jpg', 'services/066_5613c09a2cffc.jpg', '<p>Пакет &laquo;БАЗОВЫЙ&raquo; включает в себя полный пакет &laquo;СТИЛЬ+ПЛАН&raquo;<br />\r\n(с комплектом всех чертежей, планов и документов), и дополнительные услуги:</p>\r\n\r\n<ul>\r\n	<li>План привязки кухонного оборудования.</li>\r\n	<li>План привязки домашнего кинотеатра.</li>\r\n	<li>План монтажа сантехнического оборудовании.</li>\r\n	<li>План раскладки полов (по видам отделки).</li>\r\n	<li>План раскладки теплых полов.</li>\r\n	<li>План подвесных потолков (уровни потолков и материал отделки).</li>\r\n	<li>План освещения.</li>\r\n	<li>План управления освещением (схема включения/выключения источников света).</li>\r\n	<li>План установки розеток и слаботочных сетей.</li>\r\n	<li>Спецификация электроустановочных изделий (розетки, выключатели).</li>\r\n	<li>Развёртки стен (в помещениях, где укладывается настенная плитка).</li>\r\n	<li>Ведомость отделочных материалов.</li>\r\n</ul>', '', '', 1),
(3, 0, 30, 1, 0, 'ДИЗАЙН ПРОЕКТ «ПРЕМИУМ»', 'Разработка планировочных решений,  стилевая концепция, комплект рабочих чертежей, 3D визуализация, авторский надзор.', 'services/172_5613c1041dd38.jpg', 'services/172_5613c1041dfec.jpg', '<p><strong>3. Дизайн проект &laquo;ПРЕМИУМ&raquo;</strong></p>\r\n\r\n<p>Пакет &laquo;ПРЕИМУМ&raquo; включает в себя полный пакет &laquo;БАЗОВЫЙ&raquo; (с комплектом всех чертежей,<br />\r\nпланов и документов), и дополнительные услуги:</p>\r\n\r\n<ul>\r\n	<li>Визуализация интерьера (перспективы-эскизы в цвете выполненные от руки или в 3D) консультации, подбор мебели, света, сантехники, отделочных материалов, предметов интерьера.</li>\r\n	<li>Авторский надзор (архитектурное сопровождение строительства, коррекция проекта по ходу ремонта) подбор строительно-ремонтной бригады (оплачивается отдельно).</li>\r\n	<li>Чертежи нестандартных деталей декоративного оформления (камины, лестницы, ковка).</li>\r\n</ul>', '', '', 1),
(4, 0, 40, 1, 0, 'МАСТЕР-КЛАССЫ И КУРСЫ ПО ДИЗАЙНУ ИНТЕРЬЕРА', 'Открыта запись на мастер-классы и курсы по дизайну интерьера.', 'services/307_5633306b44ec4.jpg', 'services/307_5633306b48af1.jpg', '<p>Вам предстоит ремонт! Не знаете с чего начать? Приходите к нам!</p>\r\n\r\n<p>Мастер-классы по дизайну интерьера. Ответы на все ваши вопросы по дизайну,&nbsp;</p>\r\n\r\n<p>планировочным решениям и отделке Вашего помещения за 3,5 часа!</p>\r\n\r\n<p>&nbsp;Стоимость 10000 тг. (Индивидуальный мастер-класс)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Набор в группу Дизайн-интерьера экспресс. Ваш жилой интерьер за 24 часа (8 занятий по 3 часа).</p>\r\n\r\n<p>Начало ноябрь 2015. Бонус: Мастер-класс по декорированию интерьера к Новому году!&nbsp;</p>\r\n\r\n<p>Первое пробное занятие - бесплатно!</p>\r\n\r\n<p>Мы ждем вас !</p>', '', '', 0);
